import * as React from 'react';

class DrawBoard extends React.Component {
  constructor(props) {
    super(props);
    this.handleLeftClick = this.handleLeftClick.bind(this);
    this.handleRightClick = this.handleRightClick.bind(this);
    this.handleMouseMove = this.handleMouseMove.bind(this);
    this.handleCollapseClick = this.handleCollapseClick.bind(this);
    this.state = {
      context: null,
      isDraw: false,
      lines: [],
      dots: {},
      lastPoints: {},
      lastDots: {}
    };
  }

  handleLeftClick(evt) {
    if (this.state.isDraw !== true) {
      this.setState({
        isDraw: true,
        lastPoints: {
          start: {
            x: evt.nativeEvent.x,
            y: evt.nativeEvent.y
          },
          end: {
            x: evt.nativeEvent.x,
            y: evt.nativeEvent.y
          }
        }
      })
    } else {
      let point = Object.assign(
        { center: this.getCenter(this.state.lastPoints.start, this.state.lastPoints.end) },
        this.state.lastPoints
      );
      let lines = [point].concat(this.state.lines);
      this.setState({
        isDraw: false,
        lines: lines,
        dots: Object.assign(this.state.dots, this.state.lastDots),
        lastPoints: {},
        lastDots: {}
      })
    }
  }

  handleRightClick(evt) {
    evt.preventDefault();
    if (this.state.isDraw === true) {
      this.setState({
        isDraw: false,
        lastDots: {}
      })
    }
  }

  handleMouseMove(evt) {
    if (this.state.isDraw === true) {

      let lastPoint = Object.assign({}, this.state.lastPoints);
      lastPoint.end = {
        x: evt.nativeEvent.x,
        y: evt.nativeEvent.y
      };

      this.state.context.clearRect(0, 0, 500, 500);
      this.state.context.beginPath();
      this.context.moveTo(lastPoint.start.x,lastPoint.start.y);
      this.context.lineTo(lastPoint.end.x,lastPoint.end.y);
      this.context.stroke();
    }
  }

  handleCollapseClick() {

  }

  getCenter(start, end) {
    return {
      x: (start.x + end.x) / 2,
      y: (start.y + end.y) / 2
    }
  }

  render() {
    return (
      <div>
        <canvas
          style={{ border: '1px solid red'}}
          id="DrawBoardDiv"
          width='500'
          height='500'
          onClick={this.handleLeftClick}
          onContextMenu={this.handleRightClick}
          onMouseMove={this.handleMouseMove}
        />
      </div>
    )
  }
}

export default DrawBoard;
